    def test_get_block_head(self):
        self.dot_figmentRPC.request = Mock()
        mocked_response = {'jsonrpc': '2.0', 'result': {'block': {'header': {'parentHash': '0x4839fd8512a08d52c43184b527bd95013aa49fb2bdee4c71e079ea42f8cb096c', 'number': '0xb47649', 'stateRoot': '0xc6a29580df0b32399c5a7e8646c4e2517f217c8a95ee0d0cd3f5f40964958e39', 'extrinsicsRoot': '0xf45f1e525feffce9558708356c08b2cdc2c9b86635264e9cff33823b5001a3b2', 'digest': {'logs': ['0x0642414245b501036e0000009149821000000000feeefd6a5a952acd79f57acc48ec725652abc59ede333715e9fe9a3ea249f6193123a227ae240b0880ca8352cc10ef93a6376a597645318d4c2f7711f966ca08d908309d7cfddfa04f627d4c7b4883b5a2e074a541fa962077d5bb5bd81b220f', '0x05424142450101febf01d2d1860a9887fe0a11dbd96c96cbf10b6911e4d0e4bbed903c803eda1bb746473c24f7682921aca6c3c19f5159d7bf194a4f47021d76c2a41d82c0308e']}}, 'extrinsics': ['0x280403000b79369ced8201', '']}, 'justifications': None}, 'id': 1}
        self.dot_figmentRPC.request.side_effect = [mocked_response]

        block_head = self.dot_figmentRPC.get_block_head()

        assert type(block_head) == int
        assert block_head == 11826761

