    @pytest.mark.slow
    def test_get_tx_details(self):
        response = self.dot_figment.get_tx_details('0xafacc3c9c3470052842dfa2203e1732b787dcb88cf19e0250e82111f630e50ef')

        self.assertEqual(response.get('hash'), '0xafacc3c9c3470052842dfa2203e1732b787dcb88cf19e0250e82111f630e50ef')
        self.assertTrue(response.get('success'))
        self.assertEqual(len(response.get('transfers')), 1)
        self.assertTrue(response.get('transfers')[0].get('is_valid'))
        self.assertEqual(response.get('transfers')[0].get('value'), Decimal('5.0000000000'))
        self.assertEqual(response.get('fees'), Decimal('0.0132935067'))
        assert response.get('inputs') == []
        assert response.get('outputs') == []

    @pytest.mark.slow
    def test_get_tx_details_failed(self):
        response = self.dot_figment.get_tx_details('0x60dfcaf2c7ddecc11b2ec2122e27c8d17bc1a2899f38837a541441295b30c4e2')

        self.assertEqual(response.get('hash'), '0x60dfcaf2c7ddecc11b2ec2122e27c8d17bc1a2899f38837a541441295b30c4e2')
        self.assertFalse(response.get('success'))
        self.assertEqual(len(response.get('transfers')), 0)
        self.assertEqual(response.get('fees'), Decimal('0.0160674811'))
        assert response.get('inputs') == []
        assert response.get('outputs') == []
